import { createRouter, createWebHistory } from 'vue-router';
import MovimientosApp from '../components/Movimientos.vue';
import AddProduct from '../components/AddProductForm.vue';
import ConfiguracionesApp from '../components/Configuraciones.vue';
import DatosNegocios from '../components/DatosDeNegocio.vue';
import EmpleadosApp from '../components/Empleados.vue';
import ProductInventory from '../components/Productos.vue';
import NuevaVenta from '@/components/NuevaVenta.vue';
import InicioSesion from '@/components/InicioSesion.vue';
import ParteLateral from '@/components/ParteLateral.vue';
import ClientesTreinta from '@/components/ClientesTreinta.vue';
import ProvedoreProd from '@/components/ProvedoreProd.vue';

const routes = [
  { path: '/', component: InicioSesion },
  {
    path: '/Movimientos',
    component: MovimientosApp,
    props: route => ({ cajaAbierta: route.query.cajaAbierta === 'true' }),
    meta: { requiresAuth: true },
  },
  { path: '/AddProduct', component: AddProduct, meta: { requiresAuth: true } },
  { path: '/Configuracion', component: ConfiguracionesApp, meta: { requiresAuth: true } },
  { path: '/AgregarNegocio', component: DatosNegocios, meta: { requiresAuth: true } },
  { path: '/Empleados', component: EmpleadosApp, meta: { requiresAuth: true } },
  { path: '/Productos', component: ProductInventory, meta: { requiresAuth: true } },
  { path: '/NuevaVenta', component: NuevaVenta, meta: { requiresAuth: true } },
  { path: '/ClientesTreinta', component: ClientesTreinta, meta: { requiresAuth: true } },
  { path: '/ParteLateral', component: ParteLateral, meta: { requiresAuth: true } },
  { path: '/ProvedoreProd', component: ProvedoreProd, meta: { requiresAuth: true } },
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

// Navigation Guard
router.beforeEach((to, from, next) => {
  const loggedIn = localStorage.getItem('authToken'); // Aquí verificas si el usuario está autenticado
  if (to.matched.some(record => record.meta.requiresAuth) && !loggedIn) {
    next('/');
  } else {
    next();
  }
});

export default router;
