import { createApp } from 'vue'
import App from './App.vue'
import router from './router'



/* createApp(App).mount('#app') */

const app = createApp(App)

/* se pueden crear las directivas aqui */

app.directive('fontsize',{
    beforeMount  (el ) {
        el.fontSize ="70px"
    }
})

app.use(router)
app.mount('#app')
